import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { withRouter } from 'react-router';
import { compose } from 'redux';
import classNames from 'classnames';
import * as R from 'ramda';

import { getCategories, getActionCategoryId } from 'selectors';

const Categories = ({ categories, activeCategoryId }) => {
    const getActionState = R.propEq('id', activeCategoryId);
    const renderCategoty = (category, index) => {
        const linkClass = classNames({
            'list-group-item': true,
            'active': getActionState(category)
        });

        return (
            <Link
                to={`/categories/${category.id}`}
                className={linkClass}
                key={index}
            >{category.name}</Link>
        );
    };

    const renderAllCategory = () => {
        const linkClass = classNames({
            'list-group-item': true,
            'active': R.isNil(activeCategoryId)
        });

        return (
            <Link to='/' className={linkClass}>All</Link>
        );
    };

    return (
        <div className='well'>
            <h4>Brand</h4>
            <div className='list-group'>
                {renderAllCategory()}
                {categories.map((category, index) => renderCategoty(category, index))}
            </div>
        </div>
    );
};

const mapStateToProps = (state, ownProps) => ({
    categories: getCategories(state),
    activeCategoryId: getActionCategoryId(ownProps)
});

export default compose(
    withRouter,
    connect(mapStateToProps, null)
)(Categories);
